package programming_class;


public class sumOfTenTerms {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int k=1;
      int sum=0;
      for (int i=0;i<10;i++)
      {
    	  sum=sum+k;
    	  k=k+3;
    	  
    			  
      }
      System.out.println("The sum of first 10 terms are "+sum);
      
	}

}
